const formatting = {
  functions() {
    this.convertCaches = cache => {
      const {
        0: letter
      } = cache.match(/[A-Z]/);
      let sizeCache = Number(cache.replace(letter, ""));

      const cacheConversionTable = {
        B: () => sizeCache / 1000000,
        K: () => sizeCache / 1000,
        M: () => sizeCache,
        G: () => sizeCache * 1000,
      }
      const func = cacheConversionTable[letter]
      return func ? func() : cache
    }
    this.secondToMinutes = function(s) {
      const min = `0${ (s - (s %= 60)) / 60 }`.slice(-2)
      const sec = `0${s}`.slice(-2)
      return `${min}:${sec}`
    }
    this.converterToDayWeek = function() {
      const tableConversion = {
        Seg: "segunda-feira|Monday",
        Ter: "terça-feira|Tuesday",
        Qua: "quarta-feira|Wednesday",
        Qui: "quinta-feira|Thursday",
        Sex: "sexta-feira|Friday",
        Sab: "sabado|Saturday",
        Dom: "domingo|Sunday",
      }

      const newDaysWeek = []
      const daysWeek = appToolsData.hourCheck.check
      for (let week in daysWeek) {
        if (daysWeek[week] && week != "status")
          newDaysWeek.push(tableConversion[week] || week)
      }
      return newDaysWeek
    }
    //retornar o aplicativo com maior cache
    this.maiorCache = function(array) {
      return cachesConvertidos.reduce((acc, {
        name, cache
      }) => acc.cache > cache ? acc: {
        name, cache
      }, {
        name: "acode", cache: 2
      })
    }
    //retornar soma dos caches
    this.sumOfCaches = function(array) {
      return array.reduce((totalCaches, cache) =>
        totalCaches += this.convertCaches(cache),
        0).toFixed(2) + " MB"
    }
    //retornar array com nomes em ordem crescente
    this.alphabeticOrder = function() {
      appToolsData.data = [...appToolsData.data].sort((app1, app2) =>
        app1.name.toLowerCase() > app2.name.toLowerCase() ? +1: -1)
      .map((item, index) => ({
        ...item, id: index
      }))
    }
    //retornar array com nomes em ordem decrescente
    this.reverseAlphabeticOrder = function() {
      appToolsData.data = [...appToolsData.data].sort((app1, app2) => app2.name.toLowerCase() > app1.name.toLowerCase() ? +1: -1).map((item, index) => ({
        ...item, id: index
      }))
    }
  }
}

const selectorsAndEvents = {
  selectors(){
    this.c_order = document.querySelector("[data-order]");
    this.title = document.querySelector("[data-title]");
    this.c_msg = document.querySelector(".c-msg");
    this.formSearch = document.querySelector("[data-header_form]");
    this.search = document.querySelector("[data-header_search]");
    this.searchBack = document.querySelector("[data-search_back]");
    this.c_shortcuts = document.querySelector("[data-header_shortcuts]");
    this.homeShortcut = document.querySelector("[data-header_shortcut='home']");
    this.killShortcut = document.querySelector("[data-header_shortcut='kill']");
    this.c_main = document.querySelector("[data-main_pages]");
    this.c_blur = document.querySelector("[data-blur]");
    this.pages = document.querySelectorAll("[data-page]")
    this.pageHome = document.querySelector("[data-page='home']")
    this.homeRecents = document.querySelector("[data-home_recents]")
    this.homeContent = document.querySelector("[data-page_content='home']")
    this.pageKill = document.querySelector("[data-page='kill']")
    this.pageKillDetails = document.querySelector("[data-kill_card_details]")
    this.pageClean = document.querySelector("[data-page='clean']")
    this.pageCleanDetails = document.querySelector("[data-clean_card_details]")
    this.pageUninstall = document.querySelector("[data-page='uninstall']")
    this.pageUninstallDetails = document.querySelector("[data-uninstall_card_details]")
    this.pageSettings = document.querySelector("[data-page='settings']")
    this.btnStart = document.querySelector("[data-footer_card='start']")
    this.c_footer = document.querySelector("[data-footer]");
  },
  events(){
    this.optionsObserver = {
      root: this.c_main,
      rootMargin: "3px",
      threshold: .3
    }
    this.c_order.addEventListener("click", this.orderActions)
    this.c_blur.addEventListener("click", this.removeBlur)
    this.c_msg.addEventListener("click", this.removeMsg)
    this.formSearch.addEventListener("submit", this.searchApp)
    this.searchBack.addEventListener("click", this.resetSearchField)
    this.search.addEventListener("input", this.debounce(this.searchApp, 400))
    this.c_shortcuts.addEventListener("click", this.scrollToPage)
    this.observerPages = new IntersectionObserver(this.callbackObserver
    , this.optionsObserver)
    this.pageHome.addEventListener("click", this.homeActions)
    this.homeRecents.addEventListener("click", this.recentsActions)
    this.homeContent.addEventListener("change", this.toggleAutoExecutionCheck)
    this.pageSettings.addEventListener("change", this.settingsActions)
    this.pages.forEach( page => this.observerPages.observe( page ) )
    this.pageKill.addEventListener("change", this.toggleCheckbox)
    this.pageKillDetails.addEventListener("click", this.detailsActions)
    this.pageClean.addEventListener("change", this.toggleCheckbox)
    this.pageCleanDetails.addEventListener("click", this.detailsActions)
    this.pageUninstall.addEventListener("change", this.toggleCheckbox)
    this.pageUninstallDetails.addEventListener("click", this.detailsActions)
    this.c_footer.addEventListener("click", this.footerActions)
  }
}

const callbacks = {
  functions(){
    this.currentPage = "home"
    this.lastShortcut = false
    this.observerPages = false
    this.tapShortucut = false
    this.timeout = 0
    this.lastScroll = 0
    this.orderCard = 0

    this.removeBlur = () => {
      this.c_blur.classList.remove("active")
    }
    this.coverError = async (el) => {
      const value = el.getAttribute("data-cover")
      const img = `${this.path}/src/tools/system.png`
      el.src = img
      appToolsData.data[value].icon = img
      this.saveAppsData()
    }
    this.resetSearchField = async () => {
      this.search.value = ""
      this.searchBack.classList.add("hide")

      this.renderTotalCards()
      this.updateTotalNumber(this.currentPage)
    }
    this.orderActions = ({ target: el }) => {
      const { key, value } = this.getDataSetAttributes(el)
      if(key == "order") this.c_order.classList.remove("active")
      if(!value) return
      const data = appToolsData.data
      const actions = {
        alphabetic: () => {
          this.alphabeticOrder(data)
        },
        reverseAlphabetic: () => {
          this.reverseAlphabeticOrder(data)
        },
      }
      const func = actions[value]
      if(func){
        const loader = document.querySelector(".c-order__loader")
        loader.classList.add("active")
        setTimeout( () => {
          func()
          this.updatePages(["home","kill","clean", "uninstall"])
          this.saveAppsData()
          loader.classList.remove("active")
        }, 250)
      }
      console.log(value)
    }
    this.searchApp = async (e) => {
      if(this.currentPage == "settings") return
      e.preventDefault()
      const value = this.search.value.trim().toUpperCase()
      if(value != "") this.searchBack.classList.remove("hide")

      const filteredsNumber = await this.renderFilteredsCards(value)
      if(value == "") {
        this.searchBack.classList.add("hide")
        this.updateTotalNumber(this.currentPage)
        return
      }
      this.updateTotalNumber(this.currentPage, filteredsNumber, true)
    }
    this.scrollToPage = ({ target: el }) => {
      const { value } = this.getDataSetAttributes(el)
      if(!value) return
      const page = document.querySelector(`[data-page="${value}"]`)
      const to = page.offsetLeft
      this.tapShortucut = true

      if( el === this.lastShortcut ){
        page.querySelector("[data-page_content]").scroll(0, 0)
        return
      }
      this.c_main.scroll(to, 0)
      this.activateCurrentShortcut(el)
      clearTimeout(this.timeout)
      this.timeout = setTimeout( 
        () => this.tapShortucut = false, 1000 )
    }
    this.activateCurrentShortcut = (el) => {
      const { key, value } = this.getDataSetAttributes(el)
      const titles = {
        home: "App Tools",
        kill: "Encerrar aplicativos",
        clean: "Limpar caches",
        uninstall: "Desinstalar Apps",
        settings: "Configurações",
      }
      this.title.textContent = titles[value]
      const shortcut = document.querySelector(`[data-header_shortcut="${value}"]`)
      if(this.lastShortcut) 
        this.lastShortcut.classList.remove("active")
      if(shortcut){
        shortcut.classList.add("active")
      }
      this.lastShortcut = shortcut || false
      this.currentPage = value
    }
    this.callbackObserver = (entries, observer) => { 
      entries.forEach( 
      ({isIntersecting, intersectionRatio, target: el }) => {
        const elementoVisivel = isIntersecting === true || intersectionRatio > 1;

        if (elementoVisivel && !this.tapShortucut )
          this.activateCurrentShortcut(el)
          if(this.currentPage == "home" || this.currentPage == "settings") {
            this.btnStart.classList.add("disabled")
          } else {
            this.btnStart.classList.remove("disabled")
          }
      })
    }
    this.removeMsg = ({ target: el }) => {
      const { key } = this.getDataSetAttributes(el)
      if(key != "msg_cancel") return 
      const card = el.parentNode
      const { value } = this.getDataSetAttributes(card)
      if(value) clearInterval(value)
      setTimeout( () => card.remove(), 250)
    }
    this.recentsActions = ({ target: el }) => {
      const { key, value } = this.getDataSetAttributes(el)
      if(!value && key != "recents") return

      const currentHistoric = appToolsData.historic[value]
      el.classList.add("active")
      const hasActiveVibration = appToolsData.settings["vibration"]
      if(hasActiveVibration) window.navigator.vibrate(11)

      const parameters = { 
        task: "APPT 6 - ACTIONS",
        comand: currentHistoric.comand,
        value,
        time: 3000,
        el,
        page: this.currentPage
      }
      this.addToThread(parameters, appToolsData.historic)
    }
    this.homeActions = async ({ target: el }) => {
      const { key, value } = this.getDataSetAttributes(el)
      if(!key || !key.includes("home")) return
      if(key === "home_kill" || key === "home_clean") {
        el.classList.add("active")
        const hasActiveVibration = appToolsData.settings["vibration"]
        if(hasActiveVibration) window.navigator.vibrate(11)
        const parameters = { 
          task: "APPT 6 - ACTIONS",
          comand: key,
          value,
          time: 3000,
          el,
          page: this.currentPage
        }
        this.addToThread(parameters)
        return
      }
      this.detailsActions({ target: el })
    }
    this.detailsActions = async ({ target: el }) => {
      const { key } = this.getDataSetAttributes(el)
      const value = key.split("_").pop()

      const actions = {
        total: this.renderTotalCards,
        selecteds: this.renderSelectedsCards,
        exceptions: this.renderExceptionsCards,
        historic: this.renderHistoric,
        deletehistoric: this.deleteHistoric,
        execution: this.renderAutoExecution,
        stop: this.stopThread,
      }
      const func = actions[value]
      if(func) {
        if(value != "stop" ){ 
          this.resetSelectedsDetails(this.currentPage)
          detailsActive[this.currentPage] = value
        }
        el.classList.add("active")
        setTimeout( () => {
          func()
          document.querySelector(`[data-page_content="${this.currentPage}"]`).scroll(0,0)
          if(value == "stop") el.classList.remove("active")
        }, 50 )
      }
    }
    this.settingsActions = ({ target: el }) => {
      const hasActiveVibration = appToolsData.settings["vibration"]
      if(hasActiveVibration) window.navigator.vibrate(12)
      const value = el.getAttribute("data-settings_check")
      appToolsData.settings[value] = el.checked;
      this.saveAppsData()
      const settings = {

        themeDark: ()=>{},
        addSystemApps: () => {
          this.c_blur.classList.add("active")
          this.scrollToPage({target: this.homeShortcut})
          this.monitorFullAppsListing()
        },
        recents: () => {
          this.renderRecents()
        },
        activeLines: ()=>{
          document.body.style.setProperty('--size-lines-spacing', `${appToolsData.settings["activeLines"] ? ".2vh" : "0vh"}`);
        },
        updateAppsAutomatically: ()=>{},
        reset: ()=>{
          this.removeStorageData()
          this.scrollToPage({target: this.homeShortcut})
          appToolsData.settings = { ...settingsDefault }
          appToolsData.exceptions = {
            kill: [...blackList.kill],
            clean: [...blackList.clean],
            uninstall: [...blackList.uninstall]
          }
          appToolsData.historic = []
          appToolsData.hourCheck = { 
            hour: hourCheckDefault.hour,
            check: { ...hourCheckDefault.check }
          }
          detailsActive = { home: false, kill: false,  clean: false, uninstall: false }

          this.saveTaskerData("data", {...defaultTaskerData}, true)
          this.saveTaskerData("task_return", false, false)
          this.saveTaskerData("hour_execution", "00:00", false)
          tasker.toggleProfile("APPT 0 - AUTO EXECUTION", false)

          this.updateHour()
          this.updateHistoricNumber()
          this.updateCheckboxSettings()
          this.renderRecents()
          this.monitorFullAppsListing()
        },
      }
      const func = settings[value]
      if(func) func()
    }
    this.footerActions = ({ target: el }) => {
      const { value } = this.getDataSetAttributes(el)
      if(!value) return
      const actions = {
        search: this.debounce( () => {
          this.search.focus()
        }, 250 ),
        update: this.debounce( () => {
          this.monitorFullAppsListing()
        }, 250),
        start: this.debounce( () => {
          this.thread = []
          this.currentThread = 0
          const page = this.currentPage
          const validPage = ["kill","clean","uninstall"].some( item => item == page);
          if(!validPage) return
          const checkeds = document.querySelectorAll(`[data-${page}_check]:checked`);

          [...checkeds].forEach( el => {
            const { key ,value } = this.getDataSetAttributes(el)

            const parameters = { 
              task: "APPT 6 - ACTIONS",
              comand: key,
              value,
              time: 3000,
              el,
              page
            }
            this.addToThread(parameters)
          })
        }, 250),
        settings: this.debounce(() => { 
          const el = document.createElement("li")
          el.setAttribute("data-header_shortcut", "settings")
          this.scrollToPage({target: el })
        }, 250),
        order: this.debounce(() => {
          this.c_order.classList.add("active")
        }, 50),
      }
      const func = actions[value]
      if(func) func()
    }
    this.toggleAutoExecutionCheck = ({ target: el }) => {
      const { key, value } = this.getDataSetAttributes(el)
      if(!value) return
      const hasActiveVibration = appToolsData.settings["vibration"]
      if(hasActiveVibration) window.navigator.vibrate(10)
      appToolsData.hourCheck.check[value] = el.checked

      if(["Seg","Ter","Qua","Qui","Sex","Sab","Dom","kill","clean"].includes(value) || value == "status"){
        const weeks = this.converterToDayWeek().join("
")

        const taskerData = this.getHandleTaskerData("data", true)
        taskerData.days_week_execution = weeks
        taskerData.exceptions =  `${appToolsData.exceptions.kill}-MR-${appToolsData.exceptions.clean}`
        this.saveTaskerData("data", taskerData, true)
      }

      const actions = {
        status: () => {
          el.parentNode.parentNode.classList.toggle("active")
          tasker.toggleProfile("APPT 0 - AUTO EXECUTION", el.checked)
          const hour = appToolsData.hourCheck.hour

          this.saveTaskerData("hour_execution", 
            hour.replace(":","."), false)

          this.updateHour()
          const text = this.getTexts("autoExecution",el.checked)
          this.createMsg(text, true)
        },
        time: () => {
          if(!el.value) return
          appToolsData.hourCheck.hour = el.value

          this.saveTaskerData("hour_execution", 
            el.value.replace(":","."), false)

          this.updateHour()
          const text = this.getTexts("editHour", el.value)
          this.createMsg(text, true)
        }
      }
      const func = actions[value]
      if(func) func()

      this.saveAppsData()
    }
    this.toggleCheckbox = (e) => {
      const hasActiveVibration = appToolsData.settings["vibration"]
      if(hasActiveVibration) window.navigator.vibrate(12)
      const { target: el } = e
      const { value } = this.getDataSetAttributes(el)

      const card = document.querySelector(`[data-${this.currentPage}_card="${value}"]`);

      setTimeout( () => { 
        card.style.order = !el.checked ? --this.orderCard : 0
        this.toggleExceptions(value, el)
        this.saveAppsData()
        this.updateSelectedsNumber(this.currentPage)
        this.updateExceptionsNumber(this.currentPage)
      }, 200)
    }
  }
}

const ultilities = {
  functions(){
    this.order_msg = 0
    this.sleep = function(ms) {
      return new Promise( resolve => setTimeout( resolve, ms ))
    }
    this.checkSavedData = function(){
      return localStorage.hasOwnProperty("appToolsData")
    }
    this.assignSaveData = function(){
      appToolsData = JSON.parse(localStorage.getItem("appToolsData"))
    }
    this.monitorFullAppsListing = function(){
      this.c_blur.classList.add("active")
      this.scrollToPage({target: this.homeShortcut})

      let taskerData = this.getHandleTaskerData("data", true)

      taskerData.type_apps = appToolsData.settings["addSystemApps"] 
        ? `system_full` : `user_full`
      this.saveTaskerData("data", taskerData, true)

      const task = "APPT 5 - LIST APPS"
      tasker.runTask(task)

      const interval = setInterval(()=>{
        let isRunningTask = tasker.checkRunningTask(task)
        if(!isRunningTask) {
          const task_return = this.getHandleTaskerData("task_return", false)
          clearInterval(interval);
          this.updateUpdatedAppsData(task_return);
          this.saveAppsData();
          this.updatePages(["home","kill","clean","uninstall"]);
          this.createMsg(this.getTexts("updatedApps"), true);
          this.checkNewApps()
          detailsActive = { home: false, kill: false,  clean: false, uninstall: false }

          this.saveTaskerData("task_return", "false", false)
        }
      }, 200)

    }
    this.checkNewApps = async () => {
      const currentAmount = appToolsData.data.length;
      const btnUpdate = document.querySelector("[data-footer_card='update']");

      if(tasker.qtd_apps == currentAmount){
        btnUpdate.classList.remove("update")
        return 
      }

      if(appToolsData.settings.updateAppsAutomatically){
        this.monitorFullAppsListing()
        return
      }
      btnUpdate.classList.add("update");
      const text = this.getTexts("checkNewApps")
      this.createMsg(text, true, true, true)
    }
    this.createMsg = async (text, status, isLongTime, isPermanent) => {
      --this.order_msg
      const msg = this.getBodyMsg(text, status, isPermanent)
      const c_progress = msg.querySelector("div")
      this.c_msg.appendChild(msg)
      msg.classList.add("active")
      if(isPermanent) return

      let n = isLongTime ? 12 : 8
      await this.sleep(30)
      const interval = setInterval( ()=> {
        c_progress.innerText = --n
        if(n == 0) {
          msg.remove()
          clearInterval(interval)
        }
      },1000)

      msg.setAttribute("data-msg_card", interval)

    }
    this.debounce = (func, wait, immediate) => {
      let timeout;
      return function(...args) {
        const context = this;
        const later = function () {
          timeout = null;
          if (!immediate) func.apply(context, args);
        };
        const callNow = immediate && !timeout;
        clearTimeout(timeout);
        timeout = setTimeout(later, wait);
        if (callNow) func.apply(context, args);
      }
    }
    this.getDataSetAttributes = function(el){
      const data = el.dataset
      const key = Object.keys(data)[0]
      const value = data[key]
      return { data, key, value }
    }
    this.getPackagesUninstall = function(){
      return appToolsData.data.filter( ({ pakage }) => appToolsData.packagesUninstall.includes(pakage))
    }
    this.activateDevTools = function(){
      var script = document.createElement('script'); 
      script.src = "//cdn.jsdelivr.net/npm/eruda"; 
      document.body.appendChild(script); 
      script.onload = function () {
        eruda.init()
      }
    }
    this.getHandleTaskerData = function(name, isJson){
      const data = tasker.getLocal(name)
      return isJson ? JSON.parse(data) : data
    }
    this.saveTaskerData = function(name, data, isJson){
      data = isJson ? JSON.stringify(data, null, 2) : data
      tasker.setLocalVar(name, data)
    }
    this.saveAppsData = function(){
      localStorage.setItem("appToolsData", 
        JSON.stringify(appToolsData))
        //console.log("Dados salvos no storage")
    }
    this.removeStorageData = function(){
      localStorage.removeItem("appToolsData")
      const text = this.getTexts("removeStorageData")
      this.createMsg(text, true)
    }
    this.toggleExceptions = (value, el) => {
      const arr_exceptions = appToolsData.exceptions[this.currentPage]
      const packageApp = appToolsData.data[value].pakage
      const existException = arr_exceptions.includes(packageApp)
      if(existException) arr_exceptions.splice(arr_exceptions.indexOf(packageApp),1)
      if(!existException) arr_exceptions.unshift(packageApp)

      if("selecteds|exceptions".includes(detailsActive[this.currentPage])) el.parentNode.remove()

      const taskerData = this.getHandleTaskerData("data", true)
      taskerData.exceptions = `${appToolsData.exceptions.kill}-MR-${appToolsData.exceptions.clean}`
      this.saveTaskerData("data", taskerData, true)
    }
    this.createFile = function(content, nameFile){
      const code = `#!/bin/bash;
        path="${this.path}/${nameFile}.txt";
        rm "$path";
        echo "${content}" >> "$path";`
      tasker.actionsShell(code)
    }
    this.resetSelectedsDetails = function(page){
      const cards = document.querySelectorAll(`[data-${page}_card_details] li`);
        [...cards].forEach( card => card.classList.remove("active"))
    }
    this.getExceptions = function(page){
      const exceptions = appToolsData.exceptions[page]
      const data = page == "uninstall" ? this.getPackagesUninstall() : appToolsData.data
      return data.filter( ({pakage}) => exceptions.includes(pakage))
    }
    this.getSelecteds = function(page){
      const exceptions = appToolsData.exceptions[page]
      const data = page == "uninstall" ? this.getPackagesUninstall() : appToolsData.data
      return data.filter( ({pakage}) => !exceptions.includes(pakage))
    }
    this.getFiltereds = function(value){
      return appToolsData.data.filter( ({name, pakage}) =>  [name, pakage].some( item => item.toUpperCase().includes(value)))
    }
    this.renderRecents = async () => {
      if(!appToolsData.settings.recents){ 
        this.homeRecents.innerHTML = ""
        return
      }
      const recents = []
      for(let i=0; recents.length < this.numberRecents; i++){
        if(i == appToolsData.historic.length) break
        const { name, pakage, comand } = appToolsData.historic[i]
        if(recents.some( item => item.name === name && item.comand == comand ) || comand == "cleanAll" ) continue
        recents.push({ id: i, name, pakage, comand })
      }

      this.homeRecents.innerHTML = await this.getHtmlRecents(recents)
    }
    this.renderFilteredsCards = async (value) => {
      const page = this.currentPage
      const filtereds = this.getFiltereds(value)
      document.querySelector(`[data-page_content="${page}"]`)
        .innerHTML = await this.getHtmlCards( filtereds, page )
      return filtereds.length
    }
    this.renderTotalCards = async () => {
      const page = this.currentPage
      const total = page == "uninstall" ? this.getPackagesUninstall() : appToolsData.data
      document.querySelector(`[data-page_content="${page}"]`)
        .innerHTML = await this.getHtmlCards( total, page )
    }
    this.renderSelectedsCards = async () => {
      const page = this.currentPage
      const selecteds = this.getSelecteds(page)
      document.querySelector(`[data-page_content="${page}"]`)
        .innerHTML = await this.getHtmlCards( selecteds, page )
    }
    this.renderExceptionsCards = async() => {
      const page = this.currentPage
      const noSelecteds = this.getExceptions(page)
      document.querySelector(`[data-page_content="${page}"]`)
        .innerHTML = await this.getHtmlCards( noSelecteds, page )
    }
    this.deleteHistoric = () => {
      appToolsData.historic = []
      this.saveAppsData()
      this.updateHistoricNumber()
      this.renderHistoric()
      this.renderRecents()
      this.createMsg(this.getTexts("deleteHistoric"), true);
    }
    this.renderHistoric = () => {
      document.querySelector(`[data-page_content="home"]`)
        .innerHTML = this.getHtmlHistoric()
    }
    this.renderAutoExecution = () => {
      document.querySelector(`[data-page_content="home"]`)
        .innerHTML = this.getHtmlAutoExecution()
    }
    this.getTexts = function(value, boolean){
      const texts = {
        updatedApps: () => `<p>${appToolsData.settings["addSystemApps"] ? " <span>Aplicativos do sistema</span> adicionados ": " Lista de aplicativos <span>atualizada</span>" }</p>`,
        checkNewApps: () => `<p>Lista de aplicativos desatualizada, clique no botão de <span>Atualizar</span> no menu abaixo, para atualizar a lista de aplicativos.</p>`,
        removeStorageData: () => `<p>Dados do aplicativo resetados...</p>`,
        noDefined: () => `<p>Ainda não foram adicionadas ações para este botão, <span>novidades na próxima atualização</span>...</p>`,
        autoExecution: () => `<p>Execução automática <span>${boolean ? "ativada" : "desativada" }</span><p>`,
        editHour: () => `<p>Execução automática definida para às <span>${boolean}</span><p>`,
        deleteHistoric: () => `<p>Dados do <span>Histórico</span> apagados</p>`

      }
      const func = texts[value]
      if(func) return func()
    }
  }
}

