const updates = {
  functions(){
    this.updateCheckboxSettings = async function(){
      const settingsCheckbox = document.querySelectorAll("[data-settings_check]");

      settingsCheckbox.forEach( checkElement => {
        const { value } = this.getDataSetAttributes(checkElement)
        checkElement.checked = appToolsData.settings[value]
      })
      document.body.style.setProperty('--size-lines-spacing', `${appToolsData.settings["activeLines"] ? ".2vh" : "0vh"}`)
    }
    this.updateUpdatedAppsData = function(data){
      let [ appsName, appsPackage, appsUninstall] = data.split("-MR-")
      appsName = appsName.split("|")
      appsPackage = appsPackage.split("|")
      appToolsData.packagesUninstall = appsUninstall
      tasker.qtd_apps = appsName.length;

      appToolsData.data = appsName.map((name, i) => 
        ({
          id: i,
          name,
          pakage: appsPackage[i],
        })
      )
      this.alphabeticOrder()
    }
    this.updatePages = async function(pages){
      const arrDataPromises = []

      pages.forEach( page => {
        const data = page == "uninstall" ? this.getPackagesUninstall() : appToolsData.data
        arrDataPromises.push( this.getHtmlCards(data, page ) )
      })
      Promise.all(arrDataPromises)
      .then( htmlCodes => {
        pages.forEach( (page, index) => {
          const currentHtmlData = htmlCodes[index]
          document.querySelector(`[data-page_content="${page}"]`)
            .innerHTML = currentHtmlData;
          this.updateTotalNumber(page)
          if(page != "home"){
            this.updateSelectedsNumber(page)
            this.updateExceptionsNumber(page)
          }
        })
      })

      this.c_blur.classList.remove("active")
    }
    this.updateHour = function(){
      const elemHourDetails = 
        document.querySelector(`[data-home_hour]`);
      const elemHourEdit = 
        document.querySelector(`[data-hour_edit]`);
      const isActiveExecution = appToolsData.hourCheck.check.status; 
      elemHourDetails.textContent = isActiveExecution ? appToolsData.hourCheck.hour : "Off"
      if(elemHourEdit) elemHourEdit.textContent = appToolsData.hourCheck.hour
    }
    this.updateHistoricNumber = function(){
      document.querySelector(`[data-home_historic]`)
      .textContent = appToolsData.historic.length;
    }
    this.updateTotalNumber = function(page, total, isSearch){
      if(total != 0 && !total) total = appToolsData.data.length
      if(isSearch) total = total == 1 ? `${total} resultado` : `${total} resultados`
      if(page == "uninstall") total = this.getPackagesUninstall().length
      document.querySelector(`[data-${page}_total]`).textContent = total
    }
    this.updateSelectedsNumber = function(page){
      const selecteds = this.getSelecteds(page)
      document.querySelector(`[data-${page}_selecteds]`)
      .textContent = selecteds.length
    }
    this.updateExceptionsNumber = function(page){
      const exceptions = this.getExceptions(page)
      document.querySelector(`[data-${page}_exceptions]`)
        .textContent = exceptions.length
    }
    this.updateTimeNumber = function(page){
      const time = this.time
      document.querySelector(`[data-${page}_time]`)
        .textContent = this.secondToMinutes(time)
    }
    this.updateProgressNumber = function(page){
      const current = this.currentThread
      const total = this.thread.length
      document.querySelector(`[data-${page}_progress]`)
        .textContent = `${current} / ${total}`
    }
  }
}

const threadActions = {
  actions(){
    this.thread = [];
    this.caches = [];
    this.isRunningThread = false;
    this.currentThread = 0;
    this.currentInterval = 0;
    this.currentElement = false;
    this.loop = 0;
    this.time = 0;
    this.timeInterval = 0;
    this.runPage = false;


    this.callbackInterval = (task, comand, appName, appPackage) => {
      let isRunningTask = tasker.checkRunningTask(task)

      const maxNumberLoop = this.runPage == "uninstall" ? 25 : 15

      if( !isRunningTask || this.loop == maxNumberLoop ){

        const task_return = this.getHandleTaskerData("task_return")
        this.addHistoric(appName, appPackage, comand, task_return)

        if(task_return.includes("true")){ 
          this.taskActionsPerformed(task_return, comand, appName) 
          return
        }

        this.taskActionsNotPerformed(task_return, comand, appName)
        return
      }
      ++this.loop
    }
    this.addToThread = function({ task, comand, value, el, page }, array = appToolsData.data){
      const appName = array[value].name
      const appPackage = array[value].pakage

      this.thread.push( 
        async () => {
          this.loop = 0
          this.runPage = page
          this.currentElement = el
          this.saveTaskerData("task_return", "error", false)
          tasker.runTask(task, comand, appPackage)

          this.currentInterval = setInterval( () => 
            this.callbackInterval(task, comand, appName, appPackage)
          , 200 )
        }
      )
      if(!this.isRunningThread && this.thread.length)this.playThread(page)
    }
    this.playThread = function(page){
      this.isRunningThread = true
      this.time = 0
      this.caches = []
      this.runPage = page
      if(this.runPage != "home"){
        this.countTime()
        document.querySelector(`[data-${page}_details]`)
          .classList.add("hide")
        document.querySelector(`[data-${page}_card_details]`)
          .innerHTML += this.getHtmlDetails(page)
        this.btnStart.classList.add("running")
      }
      const func = this.thread[this.currentThread]
      if(func) func()
    }
    this.addHistoric = function(appName, appPackage, comand, task_return){
      appToolsData.historic.unshift( 
        { name: appName, pakage: appPackage, comand, task_return } 
      )
    }
    this.countTime = () => {
      this.timeInterval = setInterval( () => {
        ++this.time
        this.updateTimeNumber(this.runPage)
      },1000)
    }
    this.taskActionsPerformed = function(task_return, comand, appName){
      let sizeCache = 0
      if(comand.includes("clean")) {
       sizeCache = task_return.split("|")[1]
       task_return = "clean"
       if(this.runPage == "clean") this.caches.push(sizeCache)
      }
      if(comand.includes("kill")) task_return =  "kill"
      if(comand.includes("uninstall")) task_return =  "uninstall"

      const text = this.getStatusText(task_return, comand, appName, sizeCache)
      this.createMsg(text, true)
      this.checkThreadNext()
    }
    this.taskActionsNotPerformed = function(task_return, comand, appName){
      const text = this.getStatusText(task_return, comand, appName)
      this.createMsg(text, false)
      this.checkThreadNext()
    }
    this.getStatusText = function(task_return, comand, appName, sizeCache){
      const arrStatus = {
         kill: `<p><span>${appName}</span> encerrado</p>`,
         clean: `<p> Cache do <span>${appName}</span> limpo • ${sizeCache}</p>`,
         uninstall: `<p><span>${appName}</span> desinstalado</p>`,
         error: `<p>Erro ao ${ comand.includes("kill") ? "encerrar" : "limpar" } o aplicativo <span>${appName}</span></p>`,
         notCache: `<p><span>${appName}</span> não possui cache para limpar</p>`,
         notInstalled: `<p><span>${appName}</span> não está instalado. Atualize a lista de apps</p>`,
         cleanAll: `<p>Total de <span>${sizeCache}</span> de cache limpos...</p>`
      }
      const text = arrStatus[task_return]
      return text || "Não há menssagem definida para este comando"
    }
    this.checkThreadNext = async function(){
      clearInterval(this.currentInterval)
      this.currentElement.classList.remove("active")
      ++this.currentThread
      if(this.runPage != "home")this.updateProgressNumber(this.runPage)
      if(this.currentThread === this.thread.length){
        this.stopThread()
        return
      }
       const func = this.thread[this.currentThread]
       if(func) func()
    }
    this.stopThread = () => {
      clearInterval(this.timeInterval)
      if(this.runPage != "home"){
        clearInterval(this.currentInterval)
        document.querySelector(`[data-${this.runPage}_status]`).remove()
        document.querySelector(`[data-${this.runPage}_details]`)
          .classList.remove("hide")
        this.btnStart.classList.remove("running")
        this.thread = []
        this.currentThread = 0
      }
      if(this.runPage == "clean"){
        const totalCache = this.sumOfCaches(this.caches) || 0
        const text = this.getStatusText("cleanAll", "false", "false", totalCache)
        this.createMsg(text, true, true, true)
        this.addHistoric("false", "false", "cleanAll", `true|${totalCache}`)
      }
      this.caches = []
      this.time = 0
      this.isRunningThread = false
      this.updateHistoricNumber()
      this.renderRecents()
      this.saveAppsData()
    }

  }
}

const convertCaches = cache => {
      const { 0: letter} = cache.match(/[A-Z]/);
      let sizeCache = Number(cache.replace(letter, ""));

      const cacheConversionTable = {
        B: () => sizeCache / 1000000,
        K: () => sizeCache / 1000,
        M: () => sizeCache,
        G: () => sizeCache * 1000,
      }
      const func = cacheConversionTable[letter]
      return func ? func() : cache
    }

   const sumOfCaches = (array) => {
      return array.reduce( (totalCaches, cache) => 
        totalCaches += convertCaches(cache), 
      0).toFixed(2) + " MB"
    }

var total = sumOfCaches(arr_caches);
flash(total)
